import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function Products() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");
  const [message, setMessage] = useState("");
  const [quantities, setQuantities] = useState({});

  const token = localStorage.getItem("token");
  const navigate = useNavigate();
  const handleSaveToDatabase = async () => {
    if (cart.length === 0) {
      alert("Cart is empty!");
      return;
    }
  
    try {
      const response = await axios.post(
        "http://localhost:5000/api/cart",
        { cart },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
  
      if (response.status === 200) {
        alert("Cart saved successfully!");
        navigate("/payment"); // ‡πÑ‡∏õ‡∏´‡∏ô‡πâ‡∏≤‡∏ä‡∏≥‡∏£‡∏∞‡πÄ‡∏á‡∏¥‡∏ô
      }
    } catch (error) {
      console.error("Error saving cart:", error);
      alert("Failed to save cart!");
    }
  };
  useEffect(() => {
    if (!token) {
      navigate("/login");
      return;
    }

    axios
      .get("http://localhost:5000/api/products", {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((res) => {
        setProducts(res.data.products || []);
        setLoading(false);
      })
      .catch((err) => {
        setLoading(false);
        setError(`Error fetching products: ${err.message}`);
      });
  }, [token, navigate]);

  const handleSearch = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleQuantityChange = (productId, value) => {
    const quantity = Math.max(1, parseInt(value) || 1);
    setQuantities((prev) => ({ ...prev, [productId]: quantity }));
  };

  const handleAddToCart = (product) => {
    const quantity = quantities[product.ProductID] || 1;
    let cart = JSON.parse(localStorage.getItem("cart")) || [];

    const existingIndex = cart.findIndex((item) => item.ProductID === product.ProductID);
    if (existingIndex > -1) {
      cart[existingIndex].quantity += quantity;
    } else {
      cart.push({ ...product, quantity });
    }

    localStorage.setItem("cart", JSON.stringify(cart));
    setMessage(`Added ${quantity} of ${product.ProductName} to cart!`);
    setTimeout(() => setMessage(""), 2000);
  };

  const filteredProducts = products.filter((product) =>
    product.ProductID.toString().includes(searchQuery)
  );

  if (loading) {
    return (
      <div className="d-flex justify-content-center align-items-center vh-100">
        <div className="spinner-border text-primary" role="status" style={{ animationDuration: "1.5s" }}>
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4 fw-bold text-primary">üõçÔ∏è Our Products</h2>

      {error && <div className="alert alert-danger text-center">{error}</div>}
      {message && <div className="alert alert-success text-center">{message}</div>}

      <div className="mb-4 d-flex justify-content-between">
        <input
          type="text"
          className="form-control w-75 border-primary"
          placeholder="üîç Search by Product ID"
          value={searchQuery}
          onChange={handleSearch}
        />
        <button
          className="btn btn-warning ms-2 fw-bold"
          onClick={() => navigate("/cart")}
          style={{ transition: "0.3s", transform: "scale(1.05)" }}
          onMouseEnter={(e) => (e.target.style.transform = "scale(1.1)")}
          onMouseLeave={(e) => (e.target.style.transform = "scale(1.05)")}
        >
          üõí Go to Cart
        </button>
      </div>

      <div className="row">
        {filteredProducts.length > 0 ? (
          filteredProducts.map((p) => (
            <div className="col-md-4 mb-4" key={p.ProductID}>
              <div className="card shadow-lg rounded border-0">
                <div className="card-body">
                  <h5 className="card-title text-center text-success fw-bold">{p.ProductName}</h5>
                  <p className="card-text text-center text-muted">üí≤ Price: ${p.Price}</p>
                  <div className="d-flex justify-content-center align-items-center">
                    <input
                      type="number"
                      min="1"
                      className="form-control w-25 text-center border-success"
                      value={quantities[p.ProductID] || 1}
                      onChange={(e) => handleQuantityChange(p.ProductID, e.target.value)}
                    />
                    <button
                      className="btn btn-success ms-2"
                      onClick={() => handleAddToCart(p)}
                      style={{ transition: "0.3s", transform: "scale(1)" }}
                      onMouseEnter={(e) => (e.target.style.transform = "scale(1.05)")}
                      onMouseLeave={(e) => (e.target.style.transform = "scale(1)")}
                    >
                      üõçÔ∏è Add to Cart
                    </button>
                  </div>
                </div>
              </div>
            </div>
          ))
        ) : (
          <div className="col-12">
            <div className="alert alert-warning text-center">‚ö†Ô∏è No products found</div>
          </div>
        )}
      </div>
    </div>
  );
}

export default Products;
