import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

function OrderStatus() {
  const [status, setStatus] = useState("รอดำเนินการ");
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    setTimeout(() => {
      setStatus("กำลังจัดส่ง");
      setMessage("สินค้าของคุณกำลังถูกจัดส่งแล้ว!");
    }, 3000);

    setTimeout(() => {
      setStatus("จัดส่งสำเร็จ");
      setMessage("สินค้าส่งถึงปลายทางแล้ว! ขอบคุณที่ใช้บริการ");
    }, 6000);
  }, []);

  return (
    <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-md-6">
          <div className="card shadow-lg">
            <div className="card-body">
              <h2 className="text-center mb-4">สถานะคำสั่งซื้อ</h2>

              <div className={`alert text-center ${status === "จัดส่งสำเร็จ" ? "alert-success" : "alert-info"}`}>
                {message || "กำลังตรวจสอบสถานะ..."}
              </div>

              <div className="text-center">
                <h4>สถานะ: <span className="badge bg-primary">{status}</span></h4>
              </div>

              <div className="text-center mt-4">
                <button className="btn btn-outline-primary" onClick={() => navigate("/")}>
                  กลับไปหน้าหลัก
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default OrderStatus;
