import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

function Cart() {
  const [cart, setCart] = useState([]); // สินค้าในตะกร้า
  const [totalPrice, setTotalPrice] = useState(0); // ราคารวมทั้งหมด
  const navigate = useNavigate();

  useEffect(() => {
    const storedCart = JSON.parse(localStorage.getItem("cart")) || [];
  
    // ตรวจสอบว่า cart มีข้อมูลครบหรือไม่
    const cleanedCart = storedCart.map((item) => ({
      ProductID: item.ProductID,
      ProductName: item.ProductName ? item.ProductName : "ไม่มีชื่อสินค้า", // ✅ ป้องกันค่าหาย
      Quantity: item.Quantity ? Number(item.Quantity) : 1,
      Price: item.Price ? Number(item.Price) : 0, // ✅ ป้องกันค่าหาย
    }));
  
    setCart(cleanedCart);
  }, []);
  

  useEffect(() => {
    const total = cart.reduce((acc, item) => acc + item.Quantity * item.Price, 0);
    setTotalPrice(total.toFixed(2));
    localStorage.setItem("cart", JSON.stringify(cart));
  }, [cart]);

  const handleRemoveItem = (productId) => {
    const updatedCart = cart.filter(item => item.ProductID !== productId);
    setCart(updatedCart);
  };

  const handleConfirmOrder = () => {
    if (cart.length === 0) {
      alert("ตะกร้าของคุณว่างเปล่า!");
      return;
    }
    navigate("/payment");
  };

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4">ตะกร้าสินค้า</h2>
      {cart.length === 0 ? (
        <div className="alert alert-warning text-center">ตะกร้าของคุณว่างเปล่า</div>
      ) : (
        <div>
          <div className="table-responsive">
            <table className="table table-bordered">
              <thead>
                <tr>
                  <th>สินค้า</th>
                  <th>จำนวน</th>
                  <th>ราคา/หน่วย</th>
                  <th>รวม</th>
                  <th>ลบ</th>
                </tr>
              </thead>
              <tbody>
                {cart.map((item) => (
                  <tr key={item.ProductID}>
                    <td>{item.ProductName}</td>
                    <td>{item.Quantity}</td>
                    <td>{item.Price} บาท</td>
                    <td>{(item.Quantity * item.Price).toFixed(2)} บาท</td>
                    <td>
                      <button
                        className="btn btn-danger"
                        onClick={() => handleRemoveItem(item.ProductID)}
                      >
                        ลบ
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
          <div className="text-right">
            <h4>รวมทั้งหมด: {totalPrice} บาท</h4>
            <button className="btn btn-success" onClick={handleConfirmOrder}>
              ยืนยันการสั่งซื้อ
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Cart;
