import React, { useEffect, useState } from "react";
import axios from "axios";

function OrderStatus() {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/orders") // ดึงข้อมูลจาก Backend
      .then((res) => {
        setOrders(res.data);
        setLoading(false);
      })
      .catch((err) => {
        console.error("Error fetching orders:", err);
        setLoading(false);
      });
  }, []);

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4">สถานะคำสั่งซื้อ</h2>
      {loading ? (
        <p className="text-center">กำลังโหลด...</p>
      ) : orders.length > 0 ? (
        <div className="row">
          {orders.map((order) => (
            <div key={order.OrderID} className="col-md-4 mb-3">
              <div className="card shadow">
                <div className="card-body">
                  <h5 className="card-title">คำสั่งซื้อ #{order.OrderID}</h5>
                  <p className="card-text">วันที่สั่ง: {order.OrderDate}</p>
                  <p className="card-text">สถานะ: {order.Status}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <p className="text-center">ไม่มีคำสั่งซื้อ</p>
      )}
    </div>
  );
}

export default OrderStatus;
