import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

function Cart() {
  const [cart, setCart] = useState(() => {
    try {
      const storedCart = localStorage.getItem("cart");
      return storedCart ? JSON.parse(storedCart) : [];
    } catch (error) {
      console.error("Error parsing cart data:", error);
      return [];
    }
  });

  const navigate = useNavigate();

  useEffect(() => {
    localStorage.setItem("cart", JSON.stringify(cart));
  }, [cart]);

  const handleRemoveFromCart = (CartID) => {
    const updatedCart = cart.filter((item) => item.CartID !== CartID);
    setCart(updatedCart);
  };

  const handleQuantityChange = (CartID, quantity) => {
    const parsedQuantity = parseInt(quantity, 10);
    if (!isNaN(parsedQuantity) && parsedQuantity > 0) {
      const updatedCart = cart.map((item) =>
        item.CartID === CartID ? { ...item, Quantity: parsedQuantity } : item
      );
      setCart(updatedCart);
    }
  };

  const handleConfirmOrder = () => {
    // เช็คว่าในตะกร้ามีสินค้าหรือไม่
    if (cart.length === 0) {
      alert("ตะกร้าของคุณว่างเปล่า!");
      return;
    }
    // ไปยังหน้า Order Status (ติดตามสถานะการสั่งซื้อ)
    navigate("/order-status");
  };

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4">Your Cart</h2>

      {cart.length === 0 ? (
        <div className="alert alert-warning text-center">Your cart is empty.</div>
      ) : (
        <div className="row">
          {cart.map((item) => (
            <div className="col-md-4 mb-4" key={item.CartID}>
              <div className="card shadow-sm">
                <div className="card-body">
                  <h5 className="card-title">Product ID: {item.ProductID || "Unknown"}</h5>
                  <p className="card-text">
                    Quantity:{" "}
                    <input
                      type="number"
                      min="1"
                      value={item.Quantity}
                      onChange={(e) => handleQuantityChange(item.CartID, e.target.value)}
                      className="form-control w-25"
                    />
                  </p>
                  <button
                    className="btn btn-danger mt-2"
                    onClick={() => handleRemoveFromCart(item.CartID)}
                  >
                    Remove from Cart
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}

      <div className="text-center mt-4">
        {/* เพิ่มปุ่มยืนยันการสั่งซื้อ */}
        <button className="btn btn-success" onClick={handleConfirmOrder}>
          Confirm Order
        </button>
      </div>
    </div>
  );
}

export default Cart;
