import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

function Cart() {
  const [cart, setCart] = useState([]); // สินค้าในตะกร้า
  const [totalPrice, setTotalPrice] = useState(0); // ราคาทั้งหมด
  const navigate = useNavigate(); // ใช้สำหรับการนำทางไปยังหน้าต่างๆ

  useEffect(() => {
    // โหลดข้อมูลตะกร้าจาก localStorage
    const storedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCart(storedCart);

    // คำนวณราคารวมทั้งหมดของสินค้าทุกตัวในตะกร้า
    const total = storedCart.reduce((acc, item) => acc + item.Quantity * item.Price, 0);
    setTotalPrice(total);
  }, []);

  // ฟังก์ชันการยืนยันการสั่งซื้อ
  const handleConfirmOrder = () => {
    if (cart.length === 0) {
      alert("ตะกร้าของคุณว่างเปล่า!");
      return;
    }
    navigate("/payment"); // ไปยังหน้า Payment
  };

  // ฟังก์ชันการลบสินค้าออกจากตะกร้า
  const handleRemoveItem = (productId) => {
    const updatedCart = cart.filter(item => item.ProductID !== productId);
    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart)); // บันทึกตะกร้าใหม่ลงใน localStorage

    // คำนวณราคาทั้งหมดใหม่
    const total = updatedCart.reduce((acc, item) => acc + item.Quantity * item.Price, 0);
    setTotalPrice(total);
  };

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4">ตะกร้าสินค้า</h2>
      {cart.length === 0 ? (
        <div className="alert alert-warning text-center">ตะกร้าของคุณว่างเปล่า</div>
      ) : (
        <div>
          <div className="table-responsive">
            <table className="table table-bordered">
              <thead>
                <tr>
                  <th>สินค้า</th>
                  <th>จำนวน</th>
                  <th>ราคา/หน่วย</th>
                  <th>รวม</th>
                  <th>ลบ</th>
                </tr>
              </thead>
              <tbody>
                {cart.map((item) => (
                  <tr key={item.ProductID}>
                    <td>{item.ProductName}</td>
                    <td>{item.Quantity}</td>
                    <td>{item.Price} บาท</td>
                    <td>{item.Quantity * item.Price} บาท</td>
                    <td>
                      <button
                        className="btn btn-danger"
                        onClick={() => handleRemoveItem(item.ProductID)}
                      >
                        ลบ
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
          <div className="text-right">
            <h4>รวมทั้งหมด: {totalPrice} บาท</h4>
            <button className="btn btn-success" onClick={handleConfirmOrder}>
              ยืนยันการสั่งซื้อ
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Cart;
